declare module '*.yaml' {
  const src: any;
  export = src;
}

declare module '*.json' {
  const src: any;
  export default src;
}

declare module '*.png' {
  const src: any;
  export = src;
}

declare module '*.jpg' {
  const src: any;
  export = src;
}

declare module '*.svg' {
  const src: any;
  export default src;
}
